import React, { useState, useEffect, useRef } from 'react';
import io from 'socket.io-client';
import './Chatcomponent.css';
import socket from './socket';

const ChatComponent = ({ username, channel, channels, setChannels, fetchChannels }) => {
    const storedUserId = localStorage.getItem('userId');
    const storedUsername = localStorage.getItem('username');

    if (!storedUserId || !storedUsername) {
        console.warn("âš ï¸ userId ou username manquant dans localStorage !");
        alert("Votre session a expirÃ©. Veuillez vous reconnecter.");
        // Rediriger l'utilisateur vers la page de connexion si besoin
        // window.location.href = "/login";
    }

    const [message, setMessage] = useState('');
    const [messages, setMessages] = useState([]);
    const messagesEndRef = useRef(null);

    console.log("ðŸ”Ž Utilisateur :", { storedUsername, storedUserId });

    // Charger l'historique du channel actif
    useEffect(() => {
        if (channel && channel._id) {
            socket.emit('fetchChannelMessages', channel._id);
            setMessages([]); // Reset messages lors du changement de channel
        }
    }, [channel]);

    // Ã‰coute des messages du channel, messages systÃ¨me et privÃ©s
    useEffect(() => {
        const handleNewMessage = (msg) => {
            if (msg.channel === channel._id) setMessages((prev) => [...prev, msg]);
        };
        const handleSystemMessage = (msg) => {
            if (msg.channel === channel._id) setMessages((prev) => [...prev, { ...msg, system: true }]);
        };
        const handleLoadChannelMessages = (msgs) => {
            setMessages(msgs);
        };
        const handlePrivateMessage = (msg) => {
            if (msg.to === storedUserId || msg.userId === storedUserId) setMessages((prev) => [...prev, { ...msg, private: true }]);
        };
        socket.on('newMessage', handleNewMessage);
        socket.on('systemMessage', handleSystemMessage);
        socket.on('loadChannelMessages', handleLoadChannelMessages);
        socket.on('privateMessage', handlePrivateMessage);
        return () => {
            socket.off('newMessage', handleNewMessage);
            socket.off('systemMessage', handleSystemMessage);
            socket.off('loadChannelMessages', handleLoadChannelMessages);
            socket.off('privateMessage', handlePrivateMessage);
        };
    }, [channel, storedUserId]);

    // Scroll automatique
    useEffect(() => {
        messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
    }, [messages]);

    // Gestion de l'envoi de message
    const handleSendMessage = () => {
        if (!storedUserId || !storedUsername) return;
        if (message.trim() === '') return;
        if (message.startsWith('/create ')) {
            const channelName = message.split(' ')[1];
            if (!channelName) return alert('Veuillez spÃ©cifier un nom pour le canal.');
            createChannel(channelName);
            return;
        }
        if (!channel || !channel._id) {
            setMessages((prev) => [...prev, { system: true, text: 'Veuillez crÃ©er ou sÃ©lectionner un channel.' }]);
            setMessage('');
            return;
        }
        if (message.startsWith('/msg ')) {
            alert('La commande /msg (message privÃ©) n\'est pas disponible avec le mode authentification email/mot de passe.');
            setMessage('');
            return;
        }
        if (message.startsWith('/list')) {
            const parts = message.split(' ');
            const filter = parts[1] || '';
            fetch(`http://localhost:5000/api/channels${filter ? `?name=${filter}` : ''}`)
                .then(res => res.json())
                .then(data => {
                    setMessages((prev) => [...prev, { system: true, text: `Channels disponibles : ${data.map(c => c.name).join(', ')}` }]);
                });
            setMessage('');
            return;
        }
        if (message.startsWith('/users')) {
            if (!channel || !channel._id) return;
            fetch(`http://localhost:5000/api/channels/${channel._id}/users`)
                .then(res => res.json())
                .then(data => {
                    setMessages((prev) => [...prev, { system: true, text: `Utilisateurs dans ce channel : ${data.join(', ')}` }]);
                });
            setMessage('');
            return;
        }
        if (message.startsWith('/delete ')) {
            const channelName = message.split(' ')[1];
            if (!channelName) return alert('Veuillez spÃ©cifier un nom de canal Ã  supprimer.');
            const token = localStorage.getItem('token');
            fetch(`http://localhost:5000/api/channels`, {
                method: 'DELETE',
                headers: {
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${token}`,
                },
                body: JSON.stringify({ name: channelName }),
            })
                .then(res => res.json())
                .then(data => {
                    setMessages((prev) => [...prev, { system: true, text: data.message || 'Canal supprimÃ©.' }]);
                    if (fetchChannels) fetchChannels();
                });
            setMessage('');
            return;
        }
        // Message normal au channel
        socket.emit('sendMessage', {
            userId: storedUserId,
            username: storedUsername,
            channelId: channel._id,
            text: message.trim(),
        });
        setMessage('');
    };

    // CrÃ©ation de channel
    const createChannel = async (channelName) => {
        try {
            const token = localStorage.getItem('token');
            const response = await fetch('http://localhost:5000/api/channels', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${token}`,
                },
                body: JSON.stringify({ name: channelName, creator: storedUsername }),
            });
            const data = await response.json();
            if (response.ok) {
                setMessages((prev) => [...prev, { system: true, text: `Channel "${channelName}" crÃ©Ã© !` }]);
                setChannels((prev) => [...prev, data]);
                if (fetchChannels) fetchChannels();
            } else {
                setMessages((prev) => [...prev, { system: true, text: data.message || 'Erreur lors de la crÃ©ation du canal.' }]);
            }
        } catch (err) {
            setMessages((prev) => [...prev, { system: true, text: 'Erreur lors de la crÃ©ation du canal (rÃ©seau).' }]);
        }
        setMessage('');
    };

    // Gestion de la touche "EntrÃ©e"
    const handleKeyDown = (e) => {
        if (e.key === 'Enter') {
            e.preventDefault();
            handleSendMessage();
        }
    };

    return (
        <div className="chat-box">
            <div className="message-area">
                {messages.map((msg, index) => (
                    <div key={index} className={`message${msg.system ? ' system' : ''}${msg.private ? ' private' : ''}`}>
                        <p className="message-meta">
                            {msg.system ? '[SYSTÃˆME]' : msg.private ? '[PRIVÃ‰]' : msg.username} {msg.createdAt ? new Date(msg.createdAt).toLocaleString() : ''}
                        </p>
                        <p>{msg.text}</p>
                    </div>
                ))}
                <div ref={messagesEndRef} />
            </div>
            <div className="input-area">
                <input
                    type="text"
                    value={message}
                    onChange={(e) => setMessage(e.target.value)}
                    onKeyDown={handleKeyDown}
                    placeholder="Tapez votre message, /create [nom] ou /msg [pseudo] [message]..."
                />
                <button onClick={handleSendMessage}>Envoyer</button>
            </div>
        </div>
    );
};

export default ChatComponent;
